mapper.update:
UPDATE member
SET password         = #{password}
  , email            = #{email}
  , nickname         = #{nickname}
  , name             = #{name}
  , referrerNickname = #{referrerNickname}
WHERE id = #{id}

UPDATE member
SET email    = #{email}
  , nickname = #{nickname}
  , name     = #{name}
WHERE id = #{id}

mapper.select:
SELECT id FROM member WHERE id IN

    #{id}

ORDER BY id DESC

mapper.update:
UPDATE member
SET point = point + #{point}
WHERE id = #{id}

mapper.select:
select id, email
from member
where email = #{email}

